/* Import Section - Start */

/* React Imports - Start */

import React from "react";
import { useState,useEffect } from "react";
import "./../../App.css";
import projectAllocationService from '../../services/projectAllocation/ProjectAllocationService'
/* React Imports -End */

/* Import Section - End */

/* Function - Start */

const ProjectAllocation = () => {
  const [data, setData] = useState([]);

  const [searchTerm, setSearchTerm] = useState("");

  const [sortItem, setSortItem] = useState({ key: "", direction: "" });

  const handleSearch = (searchQuery) => {
    setSearchTerm(searchQuery);
  };

  const requestSort = (key) => {
    let direction = "ascending";
    if (sortItem.key === key && sortItem.direction === "ascending") {
      direction = "descending";
    }
    setSortItem({ key, direction });
  };

  const sortedData = data
    .filter((item) =>
      Object.values(item).some((value) =>
        value.toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
    )

    .sort((a, b) => {
      if (sortItem.key === "") return 0;
      const aValue = a[sortItem.key];
      const bValue = b[sortItem.key];
      if (aValue < bValue) {
        return sortItem.direction === "ascending" ? -1 : 1;
      }
      if (aValue > bValue) {
        return sortItem.direction === "ascending" ? 1 : -1;
      }
      return 0;
    });

    const projectDetails = ()=>{
      projectAllocationService.fetchData().then((res))
    }

  useEffect(() => {
        projectDetails();
     
  }, []);
  /* Render View Return - Start */

  return(

  <div className='project-content'>
      <h1>Project Allocation</h1>
      <input
          type="text"
          placeholder="Search"
          value={searchTerm}
          onChange={(e) => handleSearch(e.target.value)}
      />
      <br/><br/>
      <table>
          <thead>
              <tr>
                  <th onClick={() => requestSort('ProjectID')}>
                      Project Id
                      {sortItem.key === 'ProjectID' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
                  <th onClick={() => requestSort('ProjectName')}>
                      Project Name
                      {sortItem.key === 'ProjectName' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
                  <th onClick={() => requestSort('ProjectDescription')}>
                      Project Description
                      {sortItem.key === 'ProjectDescription' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
                  <th onClick={() => requestSort('ProjectStartDate')}>
                      Project Start Date
                      {sortItem.key === 'ProjectStartDate' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
                  <th onClick={() => requestSort('ProjectEndDate')}>
                      Project End Date
                      {sortItem.key === 'ProjectEndDate' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
                  <th onClick={() => requestSort('Status')}>
                      Project Status
                      {sortItem.key === 'Status' &&
                          (sortItem.direction === 'ascending' ? (
                              <span>&uarr;</span>
                          ) : (
                              <span>&darr;</span>
                          ))}
                  </th>
              
              </tr>
          </thead>
          <tbody>
              {sortedData.map((item) => (
                  <tr key={item.ProjectID}>
                      <td>{item.ProjectID}</td>
                      <td>{item.ProjectName}</td>
                      <td>{item.ProjectDescription}</td>
                      <td>{new Date(item.ProjectStartDate).toISOString().split('T')[0]}</td>
                      <td>{new Date(item.ProjectEndDate).toISOString().split('T')[0]}</td>
                      <td>{item.Status}</td>
                      
                  </tr>
              ))}
          </tbody>
      </table>
  </div>
  )

  /* Render View Return - End */
};

/* Function - End */

/* Export default functionName */

export default ProjectAllocation;
