import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Profileservice from '../../services/profile/ProfileService';
/* React Imports -End */

/* Import Section - End */



/* Function - Start */

const Profile = () => {
  const [empEmail, setEmpEmail] = useState("gokul@gmail.com");
  const [firstLetter, setFirstLetter] = useState('');
  const[ldata, setLdata] = useState([]);
  const [change,setChange] =useState(false);
  const [editEnabled, setEditEnabled] = useState(false);
  const [showSaveBtn, setShowSaveBtn] = useState(false);
  const [tempData,setTempData] = useState([]);

  const getprofile=()=>{
    Profileservice.getProfileData(empEmail).then((res)=>{
    setLdata(res.data)
    console.log(res.data)
    // setTempData(res.data)
    // setFirstLetter(res.data.firstname.charAt(0));
  }).catch((err)=>{
    console.log(err)
  })}
    useEffect(() => {
       getprofile();       
    },[])

    
/* Render View Return - Start */
  return (
    <>
      {sortedData.map((item) => (

<tr key={item.id}>
    <td>{item.project_id}</td>
    <td>{item.project_name}</td>
    <td>{item.project_description}</td>
    <td>{item.start_date}</td>
    <td>{item.end_date}</td>
    <td>{item.reporting_manager}</td>
    <td style={{backgroundColor:function getcolor(){
     if((item.end_date< new Date().toISOString().split("T")[0])){
      return  'red';
     } 
    }}}>{
      
      (item.end_date< new Date().toISOString().split("T")[0])?"Completed":(item.start_date>new Date().toISOString().split("T")[0])?"Upcoming":"In-Progress"}
    </td>
    
</tr>
))}
    

