                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.email} disabled/>
                        }
                    </div>
                    <div className='profile-details-innerdiv'>
                        <label><strong>Mobile Number</strong></label>
                        {!editEnabled &&
                            <span>{data.mobilenumber}</span>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.mobilenumber} onChange={(e) => {
                                setTempData({...tempData,mobilenumber:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                    <div className='profile-details-innerdiv'>
                        <label><strong>Date of Birth</strong></label>
                        {!editEnabled &&
                            <span>{data.dob}</span>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.dob} onChange={(e) => {
                                setTempData({...tempData,dob:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                    <div className='profile-details-innerdiv'>
                        <label><strong>Date of Joining</strong></label>
                        {!editEnabled &&
                            <span>{data.doj}</span>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.doj} onChange={(e) => {
                                setTempData({...tempData,doj:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                    <div className='profile-details-innerdiv'>
                        <label><strong>Work Location</strong></label>
                        {!editEnabled &&
                            <span>{data.worklocation}</span>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.worklocation} onChange={(e) => {
                                setTempData({...tempData,worklocation:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                    <div className='profile-details-innerdiv'>
                        <label><strong>Blood Group</strong></label>
                        {!editEnabled &&
                            <span>{data.bloodgroup}</span>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.bloodgroup} onChange={(e) => {
                                setTempData({...tempData,bloodgroup:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                </div>
                <div className='profile-pic-container'>
                    {data.profileimage &&
                        <img className='profile-pic' src={data.profileimage}></img>
                    }
                    {!data.profileimage &&
                        <p className='profile-pic'>{firstLetter}</p>
                    }
                    <div className='profile-person-name'>
                        {!editEnabled &&
                            <h2 className='profile-pic-letter'>{data.firstname}</h2>
                        }
                        {editEnabled &&
                            <input type="text" className='firstNameInput' value={tempData.firstname} onChange={(e) => {
                                setTempData({...tempData,firstname:e.currentTarget.value})
                            }}/>
                        }
                        &nbsp;&nbsp;
                        {!editEnabled &&
                            <h2>{data.lastname}</h2>
                        }
                        {editEnabled &&
                            <input type="text" className='lastNameInput'  value={tempData.lastname} onChange={(e) => {
                                setTempData({...tempData,lastname:e.currentTarget.value})
                            }}/>
                        }
                    </div>
                    <p className='profile-person-designation'>
                        {!editEnabled &&
                            <h2>{data.designation}</h2>
                        }
                        {editEnabled &&
                            <input type="text"  value={tempData.designation} onChange={(e) => {
                                setTempData({...tempData,designation:e.currentTarget.value})
                            }}/>
                        }
                    </p>
                    <div className='edit-btn'>
                    <button className='btn editBtn'
                        onClick={
                            ()=>{
                                setEditEnabled(!editEnabled);
                                setShowSaveBtn(!showSaveBtn);
                                if(editEnabled === true){
                                    console.log("edit true..")
                                    setTempData(data);
                                }
                            }
                        }
                    >{editEnabled === true ? 'Cancel' : 'Edit'}</button>
                    {showSaveBtn &&
                        <button className='btn saveBtn'
                            onClick={
                                ()=>{
                                    axios.put(`http://localhost:4000/profile/${empEmail}?`,{data : tempData})
                                    .then(()=>
                                    {
                                        setEditEnabled(false);
                                        setShowSaveBtn(false);
                                        setData(tempData);
                                        setChange(!change)
                                    })
                                    .catch((err)=>{
                                        console.log(err)
                                    })
                                }
                            }
                        >Save</button>
                    }
                    </div>
                </div>
            </div> */}
